import React from "react";
import CarouselHeader from "./Carousel"
import {
  Container,
  Jumbotron,
  Button,
  ButtonGroup
}
  from "reactstrap";



function LandingPageHeader() {
  let pageHeader: any = React.createRef();

  React.useEffect(() => {
    if (window.innerWidth < 991) {
      const updateScroll = () => {
        let windowScrollTop = window.pageYOffset / 3;
        pageHeader.current.style.transform =
          "translate3d(0," + windowScrollTop + "px,0)";
      };
      window.addEventListener("scroll", updateScroll);
      return function cleanup() {
        window.removeEventListener("scroll", updateScroll);
      };
    }
  });

  return (
    <>
      <div style={{ backgroundImage: "url(" + require("../assets/img/bg_landing.png") + ")" }}
        className="page-header"
        data-parallax={true}
        ref={pageHeader}
      >
        <div className="filter" />
        <Container>
          <div>
            <Jumbotron fluid>
              <CarouselHeader />
            </Jumbotron>
          </div>
          <div className="jumbotron-buttons">
            <Button className="explorer-btn" outline color="secondary">Explorer</Button>{' '}
            <Button className="toureditor-btn" outline color="secondary">Tour Editor</Button>{' '}
            <Button className="plans-btn" outline color="secondary">Plans</Button>{' '}
          </div>
        </Container>
      </div>
    </>
  );
}

export default LandingPageHeader;
